mapper.select:

select 

from sms_rule
where business_id = #{businessId,jdbcType=VARCHAR}
  and sms_type = #{smsType,jdbcType=VARCHAR}

mapper.delete:

delete from sms_rule
where business_id = #{businessId,jdbcType=VARCHAR}
  and sms_type = #{smsType,jdbcType=VARCHAR}

mapper.insert:

insert into sms_rule (business_id, sms_type, sms_template_id, 
  sms_channel_id, remark, creator, 
  created_time, editor, updated_time
  )
values (#{businessId,jdbcType=VARCHAR}, #{smsType,jdbcType=VARCHAR}, #{smsTemplateId,jdbcType=VARCHAR}, 
  #{smsChannelId,jdbcType=BIGINT}, #{remark,jdbcType=VARCHAR}, #{creator,jdbcType=VARCHAR}, 
  #{createdTime,jdbcType=TIMESTAMP}, #{editor,jdbcType=VARCHAR}, #{updatedTime,jdbcType=TIMESTAMP}
  )

mapper.update:

update sms_rule
set sms_template_id = #{smsTemplateId,jdbcType=VARCHAR},
  sms_channel_id = #{smsChannelId,jdbcType=BIGINT},
  remark = #{remark,jdbcType=VARCHAR},
  creator = #{creator,jdbcType=VARCHAR},
  created_time = #{createdTime,jdbcType=TIMESTAMP},
  editor = #{editor,jdbcType=VARCHAR},
  updated_time = #{updatedTime,jdbcType=TIMESTAMP}
where business_id = #{businessId,jdbcType=VARCHAR}
  and sms_type = #{smsType,jdbcType=VARCHAR}
